  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { aiService } from "./services/openai";
import { chatMessageSchema } from "@shared/schema";
import { z } from "zod";

interface WebSocketWithUser extends WebSocket {
  userId?: string;
  conversationId?: string;
}

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for real-time chat
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  const clients = new Map<string, WebSocketWithUser>();

  wss.on('connection', (ws: WebSocketWithUser, req) => {
    const clientId = Math.random().toString(36).substring(7);
    clients.set(clientId, ws);

    // Auto-assign a default user for demo purposes
    ws.userId = 'demo-user-' + clientId;

    console.log(`WebSocket client connected: ${clientId}`);

    // Send connection confirmation
    ws.send(JSON.stringify({
      type: 'connected',
      message: 'WebSocket connection established'
    }));

    ws.on('message', async (data) => {
      try {
        const message = JSON.parse(data.toString());
        console.log('Received WebSocket message:', message);

        if (message.type === 'chat') {
          const { message: userMessage, mode, conversationId } = message;

          // Create or get conversation
          let convId = conversationId;
          if (!convId && ws.userId) {
            const conversation = await storage.createConversation({
              userId: ws.userId,
              title: userMessage.substring(0, 50) + (userMessage.length > 50 ? '...' : ''),
              mode: mode || 'dual'
            });
            convId = conversation.id;
            ws.conversationId = convId;
          }

          // Save user message
          if (convId) {
            await storage.createMessage({
              conversationId: convId,
              role: 'user',
              content: userMessage
            });
          }

          // Get AI response
          let aiResponse;
          const recentMessages = convId ? await storage.getMessagesByConversation(convId) : [];
          const context = recentMessages.slice(-10).map(m => `${m.role}: ${m.content}`);

          switch (mode) {
            case 'career':
              aiResponse = await aiService.generateCareerResponse(userMessage, context);
              break;
            case 'health':
              aiResponse = await aiService.generateHealthResponse(userMessage, context);
              break;
            default:
              aiResponse = await aiService.generateDualResponse(userMessage, context);
          }

          // Save AI response
          if (convId) {
            await storage.createMessage({
              conversationId: convId,
              role: 'assistant',
              content: aiResponse.content,
              metadata: aiResponse.metadata
            });
          }

          // Send response to client
          if (ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify({
              type: 'chat_response',
              content: aiResponse.content,
              metadata: aiResponse.metadata,
              conversationId: convId
            }));
          }
        }

        if (message.type === 'auth') {
          ws.userId = message.userId;
        }

      } catch (error) {
        console.error('WebSocket message error:', error);
        if (ws.readyState === WebSocket.OPEN) {
          ws.send(JSON.stringify({
            type: 'error',
            message: 'Failed to process message'
          }));
        }
      }
    });

    ws.on('close', () => {
      clients.delete(clientId);
    });
  });

  // REST API routes

  // Chat endpoint (alternative to WebSocket)
  app.post("/api/chat", async (req, res) => {
    try {
      const { message, mode } = chatMessageSchema.parse(req.body);

      let aiResponse;
      switch (mode) {
        case 'career':
          aiResponse = await aiService.generateCareerResponse(message);
          break;
        case 'health':
          aiResponse = await aiService.generateHealthResponse(message);
          break;
        default:
          aiResponse = await aiService.generateDualResponse(message);
      }

      res.json(aiResponse);
    } catch (error) {
      console.error('Chat API error:', error);
      res.status(500).json({ 
        error: "Failed to process chat message",
        details: error instanceof z.ZodError ? error.errors : undefined
      });
    }
  });

  // Get conversations
  app.get("/api/conversations", async (req, res) => {
    try {
      const userId = req.query.userId as string;
      if (!userId) {
        return res.status(400).json({ error: "User ID required" });
      }

      const conversations = await storage.getConversationsByUser(userId);
      res.json(conversations);
    } catch (error) {
      console.error('Get conversations error:', error);
      res.status(500).json({ error: "Failed to fetch conversations" });
    }
  });

  // Get messages for a conversation
  app.get("/api/conversations/:id/messages", async (req, res) => {
    try {
      const { id } = req.params;
      const messages = await storage.getMessagesByConversation(id);
      res.json(messages);
    } catch (error) {
      console.error('Get messages error:', error);
      res.status(500).json({ error: "Failed to fetch messages" });
    }
  });

  // Generate AI report
  app.post("/api/reports/generate", async (req, res) => {
    try {
      const { userId, type } = req.body;

      if (!userId || !type) {
        return res.status(400).json({ error: "User ID and report type required" });
      }

      // Get user data for report generation
      const userProfile = await storage.getUserProfile(userId);
      const conversations = await storage.getConversationsByUser(userId);

      const userData = {
        profile: userProfile,
        conversations: conversations.slice(-10) // Recent conversations
      };

      const reportData = await aiService.generateAIReport(userId, type, userData);

      const report = await storage.createAiReport(userId, {
        type,
        title: reportData.title,
        content: reportData.content
      });

      res.json(report);
    } catch (error) {
      console.error('Generate report error:', error);
      res.status(500).json({ error: "Failed to generate report" });
    }
  });

  // Get user reports
  app.get("/api/reports", async (req, res) => {
    try {
      const userId = req.query.userId as string;
      if (!userId) {
        return res.status(400).json({ error: "User ID required" });
      }

      const reports = await storage.getAiReportsByUser(userId);
      res.json(reports);
    } catch (error) {
      console.error('Get reports error:', error);
      res.status(500).json({ error: "Failed to fetch reports" });
    }
  });

  // Update user profile
  app.post("/api/profile", async (req, res) => {
    try {
      const { userId, ...profileData } = req.body;

      if (!userId) {
        return res.status(400).json({ error: "User ID required" });
      }

      const profile = await storage.createOrUpdateUserProfile(userId, profileData);
      res.json(profile);
    } catch (error) {
      console.error('Update profile error:', error);
      res.status(500).json({ error: "Failed to update profile" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Conversation, 
  type InsertConversation,
  type Message, 
  type InsertMessage,
  type UserProfile,
  type InsertUserProfile,
  type AiReport,
  type InsertAiReport
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Conversation operations
  getConversation(id: string): Promise<Conversation | undefined>;
  getConversationsByUser(userId: string): Promise<Conversation[]>;
  createConversation(conversation: InsertConversation & { userId: string }): Promise<Conversation>;

  // Message operations
  getMessagesByConversation(conversationId: string): Promise<Message[]>;
  createMessage(message: InsertMessage): Promise<Message>;

  // User profile operations
  getUserProfile(userId: string): Promise<UserProfile | undefined>;
  createOrUpdateUserProfile(userId: string, profile: InsertUserProfile): Promise<UserProfile>;

  // AI reports operations
  getAiReportsByUser(userId: string): Promise<AiReport[]>;
  createAiReport(userId: string, report: InsertAiReport): Promise<AiReport>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private conversations: Map<string, Conversation>;
  private messages: Map<string, Message>;
  private userProfiles: Map<string, UserProfile>;
  private aiReports: Map<string, AiReport>;

  constructor() {
    this.users = new Map();
    this.conversations = new Map();
    this.messages = new Map();
    this.userProfiles = new Map();
    this.aiReports = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.email === email,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      id,
      createdAt: new Date()
    };
    this.users.set(id, user);
    return user;
  }

  async getConversation(id: string): Promise<Conversation | undefined> {
    return this.conversations.get(id);
  }

  async getConversationsByUser(userId: string): Promise<Conversation[]> {
    return Array.from(this.conversations.values()).filter(
      (conv) => conv.userId === userId,
    );
  }

  async createConversation(conversation: InsertConversation & { userId: string }): Promise<Conversation> {
    const id = randomUUID();
    const conv: Conversation = {
      ...conversation,
      id,
      createdAt: new Date()
    };
    this.conversations.set(id, conv);
    return conv;
  }

  async getMessagesByConversation(conversationId: string): Promise<Message[]> {
    return Array.from(this.messages.values())
      .filter((msg) => msg.conversationId === conversationId)
      .sort((a, b) => (a.createdAt?.getTime() || 0) - (b.createdAt?.getTime() || 0));
  }

  async createMessage(insertMessage: InsertMessage): Promise<Message> {
    const id = randomUUID();
    const message: Message = {
      ...insertMessage,
      id,
      createdAt: new Date()
    };
    this.messages.set(id, message);
    return message;
  }

  async getUserProfile(userId: string): Promise<UserProfile | undefined> {
    return Array.from(this.userProfiles.values()).find(
      (profile) => profile.userId === userId,
    );
  }

  async createOrUpdateUserProfile(userId: string, insertProfile: InsertUserProfile): Promise<UserProfile> {
    const existing = await this.getUserProfile(userId);

    if (existing) {
      const updated: UserProfile = {
        ...existing,
        ...insertProfile,
        updatedAt: new Date()
      };
      this.userProfiles.set(existing.id, updated);
      return updated;
    } else {
      const id = randomUUID();
      const profile: UserProfile = {
        ...insertProfile,
        id,
        userId,
        createdAt: new Date(),
        updatedAt: new Date()
      };
      this.userProfiles.set(id, profile);
      return profile;
    }
  }

  async getAiReportsByUser(userId: string): Promise<AiReport[]> {
    return Array.from(this.aiReports.values())
      .filter((report) => report.userId === userId)
      .sort((a, b) => (b.createdAt?.getTime() || 0) - (a.createdAt?.getTime() || 0));
  }

  async createAiReport(userId: string, insertReport: InsertAiReport): Promise<AiReport> {
    const id = randomUUID();
    const report: AiReport = {
      ...insertReport,
      id,
      userId,
      createdAt: new Date()
    };
    this.aiReports.set(id, report);
    return report;
  }
}

export const storage = new MemStorage();

